# taken from dataset_9_3.txt
TEXT1 = 'CTACAGCAATACGATCATATGCGGATCCGCAGTGGCCGGTAGACACACGT'
TEXT2 = 'CTACCCCGCTGCTCAATGACCGGGACTAAAGAGGCGAAGATTATGGTGTG'

def hamming_distance(text1, text2):
    if len(text1) != len(text2):
        return -1
    return sum([0 if text1[i] == text2[i] else 1 for i in range(len(text1))])

ans = hamming_distance(TEXT1, TEXT2)
print(ans)

